  "html": "\n\n<div class=\"container\">\n    <div class=\"row\">\n        <div class=\"col-md-6\">\n            \n    <div class=\"panel panel-default\">\n        \n            <div class=\"panel-heading panel-info\">\n                <h4>Random Quote</h4>\n            </div>\n        \n        <div class=\"panel-body\" itemscope itemtype=\"http://schema.org/CreativeWork\">\n            <blockquote>\n                <p itemprop=\"text\">Lee's great gifts are teaching and inspirational guidance, not administration and management.</p>\n                \n    <footer>\n        <span itemprop=\"author\" itemscope itemtype=\"http://schema.org/Person\">\n            <a href=\"http://dblquotes.com/quotes-by-cheryl-crawford\" itemprop=\"url\">\n                <span itemprop=\"name\">Cheryl Crawford</span>\n            </a>\n        </span>\n        <span itemprop=\"about\" itemscope itemtype=\"http://schema.org/Thing\">\n                    <a href=\"http://dblquotes.com/inspirational-quotes\" class=\"btn btn-xs btn-danger\" itemprop=\"url\">\n                        <span itemprop=\"name\"><cite title=\"Inspirational\">Inspirational</cite></span>\n                    </a>\n        </span>\n        \n    <a class=\"btn btn-social-icon btn-xs btn-facebook\" rel=\"external nofollow\" href=\"http://www.facebook.com/sharer/sharer.php?u=http://dblquotes.com/quote-share/38232\"><i class=\"fa fa-facebook\"></i></a>\n    <a class=\"btn btn-social-icon btn-xs btn-twitter\" rel=\"external nofollow\" href=\"https://twitter.com/intent/tweet?text=Lee%27s%20great%20gifts%20are%20teaching%20and%20inspirational%20guidance%2C%20not%20administration%20and%20management.&url=http://dblquotes.com/quote-share/38232\" data-size=\"large\"><i class=\"fa fa-twitter\"></i></a>\n    <a class=\"btn btn-social-icon btn-xs btn-linkedin\" rel=\"external nofollow\" href=\"https://www.linkedin.com/shareArticle?mini=true&url=http://dblquotes.com/quote-share/38232&title=Lee%27s%20great%20gifts%20are%20teachi